mapper.select:
select  from DS_CMS_CATEGORY a
left join DS_CMS_CATEGORY_EDIT b on a.ID=b.ID
where a.ID=#{id} and a.SITEID=#{siteid}

select  from DS_CMS_PAGE_EDIT
where SITEID=#{siteid} and STATUS in (1,8)
	 and CATEGORYID in (${idArray}) 
	 and STATUS=#{status} 
	 and IMGTOP=1 
	 and PAGETOP=1 
	 and RELEASETIME<=#{releasetime} 
	 and (TITLE like #{keyvalue, typeHandler=LikeTypeHandler} or SUMMARY like #{keyvalue, typeHandler=LikeTypeHandler} or CONTENT like #{keyvalue, typeHandler=LikeTypeHandler}) 
order by RELEASETIME ${order}
